                                  </Button>
                                </>
                              )}
                              {request.txHash && (
                                <a
                                  href={`https://solscan.io/tx/${request.txHash}?cluster=devnet`}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="text-xs text-primary hover:underline"
                                  data-testid={`view-tx-${request.id}`}
                                >
                                  View TX
                                </a>
                              )}
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                {/* Mobile Card View */}
                <div className="md:hidden">
                  {/* Mobile Select All Header */}
                  {cashoutRequests.filter((r: any) => r.status === "pending").length > 0 && (
                    <div className="flex items-center gap-3 p-4 border-b border-border bg-muted/50">
                      <Checkbox
                        checked={
                          cashoutRequests.filter((r: any) => r.status === "pending").length > 0 &&
                          cashoutRequests
                            .filter((r: any) => r.status === "pending")
                            .every((r: any) => selectedCashoutIds.includes(r.id))
                        }
                        onCheckedChange={(checked) => {
                          const pendingRequests = cashoutRequests.filter((r: any) => r.status === "pending");
                          if (checked) {
                            setSelectedCashoutIds(pendingRequests.map((r: any) => r.id));
                          } else {
                            setSelectedCashoutIds([]);
                          }
                        }}
                        data-testid="select-all-cashouts-mobile"
                      />
                      <span className="text-sm font-medium">
                        {selectedCashoutIds.length > 0 
                          ? `${selectedCashoutIds.length} selected` 
                          : 'Select all pending'}
                      </span>
                    </div>
                  )}

                  <div className="divide-y divide-border">
                    {cashoutRequests.map((request: any) => (
                      <div key={request.id} className="p-4 hover:bg-muted/30 transition-colors" data-testid={`cashout-card-${request.id}`}>
                        <div className="flex items-start justify-between mb-3">
                          <div className="flex items-center space-x-3">
                            {request.status === "pending" && (
                              <Checkbox
                                checked={selectedCashoutIds.includes(request.id)}
                                onCheckedChange={(checked) => {
                                  if (checked) {
                                    setSelectedCashoutIds([...selectedCashoutIds, request.id]);
                                  } else {
                                    setSelectedCashoutIds(selectedCashoutIds.filter(id => id !== request.id));
                                  }
                                }}
                                data-testid={`select-cashout-mobile-${request.id}`}
                              />
                            )}
                            <Avatar className="h-10 w-10">
                              <AvatarFallback>
                                {getInitials(request.user.username)}
                              </AvatarFallback>
                            </Avatar>
                            <div className="flex-1 min-w-0">
                              <div className="font-semibold truncate">
                                {request.user.username}
                              </div>
                              <div className="text-sm text-muted-foreground truncate">
                                {request.user.email}
                              </div>
                            </div>
                          </div>
                          <Badge className={getStatusColor(request.status)}>
                            {getStatusIcon(request.status)}
                            <span className="ml-1">{request.status.charAt(0).toUpperCase() + request.status.slice(1)}</span>
                          </Badge>
                        </div>

                        <div className="space-y-3 mb-3 text-sm">
                          <div>
                            <div className="text-muted-foreground text-xs">Amount</div>
                            <div className="font-semibold font-mono mt-1">
                              {request.amountGlory.toLocaleString()} GLORY
                            </div>
                            <div className="text-xs text-muted-foreground">
                              â†’ {request.amountToken} {request.tokenType}
                            </div>
                          </div>

                          <div>
                            <div className="text-muted-foreground text-xs">Withdrawal Address</div>
                            <div className="flex items-center gap-2 mt-1">
                              <div className="font-mono text-xs truncate flex-1">
                                {request.withdrawalAddress}
                              </div>
                              <Button
                                variant="ghost"
                                size="icon"
                                className="h-6 w-6 flex-shrink-0"
                                onClick={() => {
                                  navigator.clipboard.writeText(request.withdrawalAddress);
                                  toast({
                                    title: "Address Copied",
                                    description: "Withdrawal address copied to clipboard",
                                  });
                                }}
                                data-testid={`copy-address-mobile-${request.id}`}
                              >
                                <Copy className="h-3 w-3" />
                              </Button>
                            </div>
                          </div>

                          <div>
                            <div className="text-muted-foreground text-xs">Date</div>
                            <div className="mt-1">
                              {new Date(request.createdAt).toLocaleDateString()}
                            </div>
                          </div>
                        </div>

                        <div className="flex gap-2 flex-wrap">
                          {request.status === "pending" && (
                            <>
                              <Button
                                size="sm"
                                variant="outline"
                                className="flex-1 bg-success/20 text-success hover:bg-success/30 border-success/30"
                                onClick={() => approveCashoutMutation.mutate(request.id)}
                                disabled={approveCashoutMutation.isPending}
                                data-testid={`approve-cashout-mobile-${request.id}`}
                              >
                                <CheckCircle className="w-3 h-3 mr-1" />
                                Approve
                              </Button>
                              <Button
                                size="sm"
                                variant="outline"
                                className="flex-1 bg-destructive/20 text-destructive hover:bg-destructive/30 border-destructive/30"
                                onClick={() => rejectCashoutMutation.mutate(request.id)}
                                disabled={rejectCashoutMutation.isPending}
                                data-testid={`reject-cashout-mobile-${request.id}`}
                              >
                                <XCircle className="w-3 h-3 mr-1" />
                                Reject
                              </Button>
                            </>
                          )}
                          {request.txHash && (
                            <a
                              href={`https://solscan.io/tx/${request.txHash}?cluster=devnet`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-xs text-primary hover:underline flex items-center"
                              data-testid={`view-tx-mobile-${request.id}`}
                            >
                              View TX
                            </a>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {cashoutRequests.length === 0 && (
                  <div className="text-center py-12" data-testid="no-cashouts">
                    <DollarSign className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">No cashout requests</h3>
                    <p className="text-muted-foreground">Cashout requests will appear here when users request them.</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          {/* Audit Logs Tab */}
          <TabsContent value="audit" className="space-y-4" data-testid="audit-tab">
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle>Audit Logs</CardTitle>
                  <Button
                    size="sm"
                    variant="outline"
                    className="bg-destructive/20 text-destructive hover:bg-destructive/30 border-destructive/30"
                    onClick={() => setClearLogsDialogOpen(true)}
                    data-testid="button-clear-logs"
                  >
                    <Trash2 className="w-4 h-4 mr-2" />
                    Clear All Logs
                  </Button>
                </div>
              </CardHeader>
              <CardContent className="p-0">
                {/* Desktop Table View */}
                <div className="overflow-x-auto hidden md:block">
                  <table className="w-full" data-testid="audit-logs-table">
                    <thead className="bg-muted">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-muted-foreground uppercase tracking-wider">
                          Date
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-muted-foreground uppercase tracking-wider">
                          Admin
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-muted-foreground uppercase tracking-wider">
                          Action
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-semibold text-muted-foreground uppercase tracking-wider">
                          Details
                        </th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-border">
                      {auditLogs.map((log: any, index: number) => (
                        <tr key={log.id} className="hover:bg-muted/30 transition-colors" data-testid={`audit-log-${index}`}>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-muted-foreground">
                            {new Date(log.createdAt).toLocaleDateString()} {new Date(log.createdAt).toLocaleTimeString()}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium" data-testid={`log-admin-${index}`}>
                            Admin #{log.actorUserId.substring(0, 8)}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap" data-testid={`log-action-${index}`}>
                            <Badge variant="outline" className="text-xs">
                              {log.action.replace(/_/g, " ").toLowerCase()}
                            </Badge>
                          </td>
                          <td className="px-6 py-4 text-sm text-muted-foreground" data-testid={`log-details-${index}`}>
                            {log.meta ? JSON.stringify(log.meta).substring(0, 100) + "..." : "No details"}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                {/* Mobile Card View */}
                <div className="md:hidden divide-y divide-border">
                  {auditLogs.map((log: any, index: number) => (
                    <div key={log.id} className="p-4 hover:bg-muted/30 transition-colors" data-testid={`audit-log-card-${index}`}>
                      <div className="flex items-start justify-between mb-3">
                        <div className="flex-1">
                          <div className="text-xs text-muted-foreground mb-1">
                            {new Date(log.createdAt).toLocaleDateString()} {new Date(log.createdAt).toLocaleTimeString()}
                          </div>
                          <div className="font-medium text-sm">
                            Admin #{log.actorUserId.substring(0, 8)}
                          </div>
                        </div>
                        <Badge variant="outline" className="text-xs">
                          {log.action.replace(/_/g, " ").toLowerCase()}
                        </Badge>
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {log.meta ? JSON.stringify(log.meta).substring(0, 100) + "..." : "No details"}
                      </div>
                    </div>
                  ))}
                </div>

                {auditLogs.length === 0 && (
                  <div className="text-center py-12" data-testid="no-audit-logs">
                    <BarChart3 className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">No audit logs</h3>
                    <p className="text-muted-foreground">Admin actions will appear here.</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>

      <CreateContestModal
        isOpen={isCreateContestModalOpen}
        onClose={() => setIsCreateContestModalOpen(false)}
        onSubmit={(formData) => createContestMutation.mutate(formData)}
      />

      {/* Bulk Delete Confirmation Dialog */}
      <Dialog open={bulkDeleteDialogOpen} onOpenChange={setBulkDeleteDialogOpen}>
        <DialogContent data-testid="bulk-delete-dialog">
          <DialogHeader>
            <DialogTitle className="text-destructive">Delete Selected Users</DialogTitle>
            <DialogDescription asChild>
              <div className="space-y-3">
                <p>
                  You are about to permanently delete <span className="font-semibold">{selectedUserIds.length}</span> users.
                </p>
                <div>
                  <p className="text-destructive font-medium mb-2">
                    This will also delete ALL associated data including:
                  </p>
                  <ul className="list-disc list-inside space-y-1 text-sm ml-4 text-muted-foreground">
                    <li>All submissions and media files</li>
                    <li>All votes and interactions</li>
                    <li>GLORY balance and transaction history</li>
                    <li>Profile data and settings</li>
                    <li>Cashout requests</li>
                  </ul>
                </div>
                <p className="font-semibold text-destructive">
                  This action cannot be undone!
                </p>
              </div>
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="confirmDelete">
                Type <span className="font-mono font-bold">DELETE</span> to confirm
              </Label>
              <Input
                id="confirmDelete"
                placeholder="Type DELETE to confirm"
                value={deleteConfirmText}
                onChange={(e) => setDeleteConfirmText(e.target.value)}
                data-testid="confirm-delete-input"
              />
            </div>
            <div className="bg-muted p-3 rounded-md">
              <h4 className="font-semibold text-sm mb-2">Selected users:</h4>
              <div className="max-h-32 overflow-y-auto space-y-1">
                {selectedUserIds.map(userId => {
                  const user = filteredUsers.find((u: any) => u.id === userId);
                  return (
                    <div key={userId} className="text-sm text-muted-foreground">
                      {user?.username} ({user?.email})
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setBulkDeleteDialogOpen(false);
                setDeleteConfirmText("");
              }}
              data-testid="cancel-bulk-delete"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={confirmBulkDelete}
              disabled={bulkDeleteUsersMutation.isPending || deleteConfirmText !== "DELETE"}
              data-testid="confirm-bulk-delete"
            >
              {bulkDeleteUsersMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Deleting...
                </>
              ) : (
                <>
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete {selectedUserIds.length} Users
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Bulk Delete Contests Confirmation Dialog */}
      <Dialog open={bulkDeleteContestsDialogOpen} onOpenChange={setBulkDeleteContestsDialogOpen}>
        <DialogContent data-testid="bulk-delete-contests-dialog">
          <DialogHeader>
            <DialogTitle className="text-destructive">Delete Selected Contests</DialogTitle>
            <DialogDescription asChild>
              <div className="space-y-3">
                <p>
                  You are about to permanently delete <span className="font-semibold">{selectedContestIds.length}</span> contests.
                </p>
                <div>
                  <p className="text-destructive font-medium mb-2">
                    This will also delete ALL associated data including:
                  </p>
                  <ul className="list-disc list-inside space-y-1 text-sm ml-4 text-muted-foreground">
                    <li>All submissions in these contests</li>
                    <li>All votes on these submissions</li>
                    <li>All media files associated with submissions</li>
                    <li>Prize pool and reward distribution data</li>
                  </ul>
                </div>
                <p className="font-semibold text-destructive">
                  This action cannot be undone!
                </p>
              </div>
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="confirmDeleteContests">
                Type <span className="font-mono font-bold">DELETE</span> to confirm
              </Label>
              <Input
                id="confirmDeleteContests"
                placeholder="Type DELETE to confirm"
                value={deleteContestsConfirmText}
                onChange={(e) => setDeleteContestsConfirmText(e.target.value)}
                data-testid="confirm-delete-contests-input"
              />
            </div>
            <div className="bg-muted p-3 rounded-md">
              <h4 className="font-semibold text-sm mb-2">Selected contests:</h4>
              <div className="max-h-32 overflow-y-auto space-y-1">
                {selectedContestIds.map(contestId => {
                  const contest = contests.find((c: any) => c.id === contestId);
                  return (
                    <div key={contestId} className="text-sm text-muted-foreground">
                      {contest?.title} ({contest?.status})
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setBulkDeleteContestsDialogOpen(false);
                setDeleteContestsConfirmText("");
              }}
              data-testid="cancel-bulk-delete-contests"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={confirmBulkDeleteContests}
              disabled={bulkDeleteContestsMutation.isPending || deleteContestsConfirmText !== "DELETE"}
              data-testid="confirm-bulk-delete-contests"
            >
              {bulkDeleteContestsMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Deleting...
                </>
              ) : (
                <>
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete {selectedContestIds.length} Contests
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Clear Audit Logs Confirmation Dialog */}
      <Dialog open={clearLogsDialogOpen} onOpenChange={setClearLogsDialogOpen}>
        <DialogContent data-testid="clear-logs-dialog">
          <DialogHeader>
            <DialogTitle className="text-destructive">Clear All Audit Logs</DialogTitle>
            <DialogDescription asChild>
              <div className="space-y-3">
                <p>
                  You are about to permanently delete <span className="font-semibold">all audit logs</span>.
                </p>
                <p className="text-destructive font-medium">
                  This will remove all historical records of admin actions and system events.
                </p>
                <p className="font-semibold text-destructive">
                  This action cannot be undone!
                </p>
              </div>
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="confirmClearLogs">
                Type <span className="font-mono font-bold">CLEAR</span> to confirm
              </Label>
              <Input
                id="confirmClearLogs"
                placeholder="Type CLEAR to confirm"
                value={clearLogsConfirmText}
                onChange={(e) => setClearLogsConfirmText(e.target.value)}
                data-testid="confirm-clear-logs-input"
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setClearLogsDialogOpen(false);
                setClearLogsConfirmText("");
              }}
              data-testid="cancel-clear-logs"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={() => clearAuditLogsMutation.mutate()}
              disabled={clearAuditLogsMutation.isPending || clearLogsConfirmText !== "CLEAR"}
              data-testid="confirm-clear-logs"
            >
              {clearAuditLogsMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Clearing...
                </>
              ) : (
                <>
                  <Trash2 className="h-4 w-4 mr-2" />
                  Clear All Logs
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Balance Edit Dialog */}
      <Dialog open={gloryEditDialogOpen} onOpenChange={(open) => {
        setGloryEditDialogOpen(open);
        if (!open) {
          // Reset state when dialog closes
          setGloryAmountInput("");
          setSelectedUserId("");
          setSelectedCurrency("GLORY");
        }
      }}>
        <DialogContent data-testid="balance-edit-dialog">
          <DialogHeader>
            <DialogTitle className="flex items-center">
              <Crown className="w-5 h-5 mr-2 text-primary" />
              Edit User Balance
            </DialogTitle>
            <DialogDescription asChild>
              <div className="space-y-2">
                <p>
                  Edit the balance for the selected user.
                </p>
                <div className="bg-muted p-3 rounded-md text-sm">
                  <p className="font-medium mb-2">Supported formats:</p>
                  <ul className="space-y-1 text-muted-foreground">
                    <li><code className="bg-background px-1 rounded">300</code> - Set balance to exactly 300</li>
                    <li><code className="bg-background px-1 rounded">+50</code> - Add 50 to current balance</li>
                    <li><code className="bg-background px-1 rounded">-20</code> - Subtract 20 from current balance</li>
                  </ul>
                </div>
              </div>
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="currency">Currency</Label>
              <Select value={selectedCurrency} onValueChange={(value) => setSelectedCurrency(value as "GLORY" | "SOL" | "USDC")}>
                <SelectTrigger id="currency" data-testid="select-balance-currency">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="GLORY">GLORY</SelectItem>
                  <SelectItem value="SOL">SOL</SelectItem>
                  <SelectItem value="USDC">USDC</SelectItem>
                </SelectContent>
              </Select>
            </div>
            {selectedUserId && (
              <div className="bg-muted p-3 rounded-md">
                <div className="text-sm">
                  <span className="text-muted-foreground">Current {selectedCurrency} balance:</span>
                  <span className="font-mono font-semibold ml-2">
                    {(() => {
                      const userBalance = filteredUsers.find((u: any) => u.id === selectedUserId);
                      const balance = selectedCurrency === 'SOL' ? userBalance?.solBalance : 
                                     selectedCurrency === 'USDC' ? userBalance?.usdcBalance : 
                                     userBalance?.gloryBalance;
                      return (balance || 0).toLocaleString();
                    })()} {selectedCurrency}
                  </span>
                </div>
              </div>
            )}
            <div className="space-y-2">
              <Label htmlFor="gloryAmount">New {selectedCurrency} Amount</Label>
              <Input
                id="gloryAmount"
                placeholder="e.g., 300, +50, -20"
                value={gloryAmountInput}
                onChange={(e) => setGloryAmountInput(e.target.value)}
                data-testid="glory-amount-input"
              />
              <p className="text-xs text-muted-foreground">
                Enter a number to set exact balance, or use +/- to add/subtract
              </p>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setGloryEditDialogOpen(false);
                setGloryAmountInput("");
                setSelectedUserId("");
                setSelectedCurrency("GLORY");
              }}
              data-testid="cancel-balance-edit"
            >
              Cancel
            </Button>
            <Button
              onClick={handleGloryBalanceUpdate}
              disabled={updateGloryBalanceMutation.isPending || !gloryAmountInput.trim()}
              data-testid="confirm-glory-edit"
              className="bg-primary/20 text-primary hover:bg-primary/30 border-primary/30"
            >
              {updateGloryBalanceMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Updating...
                </>
              ) : (
                <>
                  <Crown className="h-4 w-4 mr-2" />
                  Update Balance
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

drizzle.config.ts.content:
import { defineConfig } from "drizzle-kit";

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL, ensure the database is provisioned");
}

export default defineConfig({
  out: "./migrations",
  schema: "./shared/schema.ts",
  dialect: "postgresql",
  dbCredentials: {
    url: process.env.DATABASE_URL,
  },
});

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import cookieParser from "cookie-parser";
import * as ed25519 from "@noble/ed25519";
import { storage } from "./storage";
import { authenticateToken, requireAdmin, requireApproved, generateToken, type AuthRequest } from "./middleware/auth";
import { votingRateLimiter } from "./services/rate-limiter";
import { upload, uploadFile, deleteFile } from "./services/file-upload";
import { calculateRewardDistribution } from "./services/reward-distribution";
import { ContestScheduler } from "./contest-scheduler";
import { verifyTransaction, solanaConnection } from "./solana";
import { findReference } from "@solana/pay";
import { PublicKey } from "@solana/web3.js";
import { z } from "zod";
import { 
  loginSchema, 
  registerSchema, 
  voteSubmissionSchema,
  updateUserStatusSchema,
  updateSubmissionStatusSchema,
  updateWithdrawalAddressSchema,
  bulkSubmissionIdsSchema,
  insertContestSchema,
  insertSubmissionSchema,
  connectWalletSchema,
  createCashoutRequestSchema,
  updateCashoutStatusSchema,
  approveCashoutSchema,
  rejectCashoutSchema,
  bulkCashoutIdsSchema,
  bulkRejectCashoutSchema,
  insertSiteSettingsSchema
} from "@shared/schema";

// Create contest scheduler instance
export const contestScheduler = new ContestScheduler(storage);

export async function registerRoutes(app: Express): Promise<Server> {
  app.use(cookieParser());

  // Initialize contest scheduler
  contestScheduler.initialize().catch(err => {
    console.error("Failed to initialize contest scheduler:", err);
  });

  // Track recent GLORY balance requests to prevent duplicates
  const recentGloryRequests = new Map<string, number>();



  // Serve uploaded files from public/uploads directory
  const express = await import("express");
  const path = await import("path");
  app.use("/uploads", express.default.static(path.join(process.cwd(), "public", "uploads")));

  // Auth routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const { username, email, password } = registerSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ error: "User with this email already exists" });
      }

      const existingUsername = await storage.getUserByUsername(username);
      if (existingUsername) {
        return res.status(400).json({ error: "Username already taken" });
      }

      // Hash password
      const passwordHash = await bcrypt.hash(password, 10);

      // Create user
      const user = await storage.createUser({
        username,
        email,
        passwordHash,
        role: "user",
        status: "pending" // Requires admin approval
      });

      res.status(201).json({ 
        message: "User created successfully. Please wait for admin approval.",
        user: { id: user.id, username: user.username, email: user.email, status: user.status }
      });
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid input" });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = loginSchema.parse(req.body);

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      const validPassword = await bcrypt.compare(password, user.passwordHash);
      if (!validPassword) {
        return res.status(401).json({ error: "Invalid credentials" });
      }

      if (user.status === "banned") {
        return res.status(403).json({ error: "Account is banned" });
      }

      // Generate JWT token
      const token = generateToken({
        id: user.id,
        email: user.email,
        role: user.role,
        status: user.status
      });

      // Set httpOnly cookie
      res.cookie("authToken", token, {
        httpOnly: true,
        secure: process.env.NODE_ENV === "production",
        sameSite: "lax",
        maxAge: 7 * 24 * 60 * 60 * 1000 // 7 days
      });

      res.json({ 
        user: { 
          id: user.id, 
          username: user.username, 
          email: user.email, 
          role: user.role, 
          status: user.status,
          gloryBalance: user.gloryBalance
        }
      });
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid input" });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    res.clearCookie("authToken");
    res.json({ message: "Logged out successfully" });
  });

  app.get("/api/me", authenticateToken, async (req: AuthRequest, res) => {
    const user = await storage.getUser(req.user!.id);
    if (!user) {
      return res.status(404).json({ error: "User not found" });
    }

    res.json({
      id: user.id,
      username: user.username,
      email: user.email,
      role: user.role,
      status: user.status,
      gloryBalance: user.gloryBalance,
      solBalance: user.solBalance,
      usdcBalance: user.usdcBalance,
      avatarUrl: user.avatarUrl,
      withdrawalAddress: user.withdrawalAddress,
      createdAt: user.createdAt
    });
  });

  app.get("/api/me/submissions", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const { status } = req.query;
      const filters: any = { userId: req.user!.id };

      if (status && status !== 'all') {
        filters.status = status as string;
      }

      const submissions = await storage.getSubmissions(filters);
      res.json(submissions);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch submissions" });
    }
  });

  // Update profile (username)
  app.patch("/api/me", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const { username } = req.body;
      const userId = req.user!.id;

      if (!username || username.trim().length < 3) {
        return res.status(400).json({ error: "Username must be at least 3 characters" });
      }

      // Check if username is already taken by another user
      const existingUser = await storage.getUserByUsername(username);
      if (existingUser && existingUser.id !== userId) {
        return res.status(400).json({ error: "Username already taken" });
      }

      await storage.updateUser(userId, { username: username.trim() });
      const updatedUser = await storage.getUser(userId);

      res.json({
        id: updatedUser!.id,
        username: updatedUser!.username,
        email: updatedUser!.email,
        role: updatedUser!.role,
        status: updatedUser!.status,
        gloryBalance: updatedUser!.gloryBalance,
        avatarUrl: updatedUser!.avatarUrl,
        createdAt: updatedUser!.createdAt
      });
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Failed to update profile" });
    }
  });

  // Upload/update avatar
  app.post("/api/me/avatar", authenticateToken, upload.single("avatar"), async (req: AuthRequest, res) => {
    try {
      const userId = req.user!.id;

      if (!req.file) {
        return res.status(400).json({ error: "No file uploaded" });
      }

      const { url } = await uploadFile(req.file);

      await storage.updateUser(userId, { avatarUrl: url });
      const updatedUser = await storage.getUser(userId);

      res.json({
        id: updatedUser!.id,
        username: updatedUser!.username,
        email: updatedUser!.email,
        role: updatedUser!.role,
        status: updatedUser!.status,
        gloryBalance: updatedUser!.gloryBalance,
        avatarUrl: updatedUser!.avatarUrl,
        createdAt: updatedUser!.createdAt
      });
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : "Failed to upload avatar" });
    }
  });

  // Delete profile
  app.delete("/api/me", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const userId = req.user!.id;

      // Delete user (cascade will handle related data)
      await storage.deleteUser(userId);

      // Clear auth cookie
      res.clearCookie("authToken");

      res.json({ message: "Profile deleted successfully" });
    } catch (error) {
      res.status(500).json({ error: error instanceof Error ? error.message : "Failed to delete profile" });
    }
  });

  // Update withdrawal address
  app.patch("/api/users/withdrawal-address", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const { address } = updateWithdrawalAddressSchema.parse(req.body);
      const userId = req.user!.id;

      const updatedUser = await storage.updateWithdrawalAddress(userId, address);

      if (!updatedUser) {
        return res.status(404).json({ error: "User not found" });
      }

      res.json({ 
        success: true, 
        withdrawalAddress: updatedUser.withdrawalAddress 
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid withdrawal address format" });
      }
      res.status(500).json({ error: error instanceof Error ? error.message : "Failed to update withdrawal address" });
    }
  });

  // Wallet routes
  app.post("/api/wallet/connect", authenticateToken, requireApproved, async (req: AuthRequest, res) => {
    try {
      const { address, provider, signature, message } = connectWalletSchema.parse(req.body);
      const userId = req.user!.id;

      // Check if wallet is already connected to another user
      const existingWallet = await storage.getUserWalletByAddress(address);
      if (existingWallet && existingWallet.userId !== userId) {
        return res.status(400).json({ error: "This wallet is already connected to another account" });
      }

      // Check if user already has a wallet
      const userWallet = await storage.getUserWallet(userId);
      if (userWallet) {
        return res.status(400).json({ error: "User already has a connected wallet" });
      }

      // Verify signature
      try {
        const messageBytes = new TextEncoder().encode(message);
        const signatureBytes = Buffer.from(signature, 'base64');
        const publicKeyBytes = Buffer.from(address, 'base64');

        const isValid = await ed25519.verify(signatureBytes, messageBytes, publicKeyBytes);

        if (!isValid) {
          return res.status(400).json({ error: "Invalid signature" });
        }
      } catch (error) {
        return res.status(400).json({ error: "Signature verification failed" });
      }

      // Create wallet
      const wallet = await storage.createUserWallet({
        userId,
        address,
        provider,
        status: "active",
        verifiedAt: new Date()
      });

      res.json({ wallet });
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Failed to connect wallet" });
    }
  });

  app.get("/api/wallet/me", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const wallet = await storage.getUserWallet(req.user!.id);
      res.json({ wallet });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch wallet" });
    }
  });

  // Solana payment verification
  const verifySolanaPaymentSchema = z.object({
    signature: z.string(),
    expectedAmount: z.number().positive(),
    recipientAddress: z.string(),
    contestId: z.string().uuid().optional(),
    submissionId: z.string().uuid().optional(),
  });

  app.post("/api/payment/verify-solana", authenticateToken, requireApproved, async (req: AuthRequest, res) => {
    try {
      const { signature, expectedAmount, recipientAddress, contestId, submissionId } = 
        verifySolanaPaymentSchema.parse(req.body);

      const userId = req.user!.id;

      // Get user's connected wallet
      const userWallet = await storage.getUserWallet(userId);
      if (!userWallet) {
        return res.status(400).json({ error: "No wallet connected. Please connect your Solana wallet first." });
      }

      // Check if transaction already used (prevent replay attacks)
      const existingTx = await storage.getGloryTransactionByHash(signature);
      if (existingTx) {
        return res.status(400).json({ error: "Transaction already verified. Each transaction can only be used once." });
      }

      // Verify transaction on Solana blockchain
      const txResult = await verifyTransaction(signature);

      if (!txResult.confirmed) {
        return res.status(400).json({ error: "Transaction not found or not confirmed on Solana blockchain" });
      }

      // Verify payer matches user's connected wallet
      if (txResult.from !== userWallet.address) {
        return res.status(400).json({ 
          error: `Transaction payer mismatch. Expected ${userWallet.address}, got ${txResult.from}` 
        });
      }

      // Verify transaction details
      if (!txResult.amount || txResult.amount < expectedAmount) {
        return res.status(400).json({ 
          error: `Insufficient payment amount. Expected ${expectedAmount} SOL, received ${txResult.amount || 0} SOL` 
        });
      }

      if (txResult.to !== recipientAddress) {
        return res.status(400).json({ 
          error: "Payment recipient address mismatch" 
        });
      }

      // Record transaction in glory ledger
      await storage.createGloryTransaction({
        userId,
        delta: 0, // Crypto payments don't affect GLORY balance
        currency: "SOL",
        reason: `Solana payment verified - ${expectedAmount} SOL`,
        contestId: contestId || null,
        submissionId: submissionId || null,
        txHash: signature,
        metadata: {
          from: txResult.from,
          to: txResult.to,
          amount: txResult.amount,
          verifiedAt: new Date().toISOString(),
        }
      });

      res.json({ 
        success: true, 
        transaction: {
          signature,
          amount: txResult.amount,
          from: txResult.from,
          to: txResult.to,
        }
      });
    } catch (error) {
      console.error("Solana payment verification error:", error);
      res.status(400).json({ 
        error: error instanceof Error ? error.message : "Failed to verify Solana payment" 
      });
    }
  });

  // Find payment by reference (Solana Pay reference tracking)
  const findPaymentByReferenceSchema = z.object({
    reference: z.string(), // Base58 public key
    expectedAmount: z.number().positive(),
    recipientAddress: z.string(),
    contestId: z.string().uuid().optional(),
    submissionId: z.string().uuid().optional(),
  });

  app.post("/api/payment/find-by-reference", authenticateToken, requireApproved, async (req: AuthRequest, res) => {
    try {
      const { reference, expectedAmount, recipientAddress, contestId, submissionId } = 
        findPaymentByReferenceSchema.parse(req.body);

      const userId = req.user!.id;

      // Get user's connected wallet
      const userWallet = await storage.getUserWallet(userId);
      if (!userWallet) {
        return res.status(400).json({ error: "No wallet connected. Please connect your Solana wallet first." });
      }

      // Convert reference string to PublicKey
      const referenceKey = new PublicKey(reference);

      // Find transaction using reference
      const signatureInfo = await findReference(solanaConnection, referenceKey, { finality: 'confirmed' });

      if (!signatureInfo || !signatureInfo.signature) {
        return res.json({ found: false, message: "Payment not found yet. Please complete the transaction in your wallet." });
      }

      const signature = signatureInfo.signature;

      // Check if transaction already processed
      const existingTx = await storage.getGloryTransactionByHash(signature);
      if (existingTx) {
        return res.json({ 
          found: true, 
          alreadyProcessed: true,
          txHash: signature,
          message: "Payment already verified" 
        });
      }

      // Verify transaction details
      const txResult = await verifyTransaction(signature);

      if (!txResult.confirmed) {
        return res.json({ found: false, message: "Transaction found but not yet confirmed" });
      }

      // Verify payer matches user's connected wallet
      if (txResult.from !== userWallet.address) {
        return res.status(400).json({ 
          error: `Transaction payer mismatch. Expected ${userWallet.address}, got ${txResult.from}` 
        });
      }

      // Verify transaction details
      if (!txResult.amount || txResult.amount < expectedAmount) {
        return res.status(400).json({ 
          error: `Insufficient payment amount. Expected ${expectedAmount} SOL, received ${txResult.amount || 0} SOL` 
        });
      }

      if (txResult.to !== recipientAddress) {
        return res.status(400).json({ 
          error: "Payment recipient address mismatch" 
        });
      }

      // Record transaction in glory ledger
      await storage.createGloryTransaction({
        userId,
        delta: 0, // Crypto payments don't affect GLORY balance
        currency: "SOL",
        reason: `Solana payment verified via reference - ${expectedAmount} SOL`,
        contestId: contestId || null,
        submissionId: submissionId || null,
        txHash: signature,
        metadata: {
          reference,
          from: txResult.from,
          to: txResult.to,
          amount: txResult.amount,
          verifiedAt: new Date().toISOString(),
        }
      });

      res.json({ 
        found: true,
        alreadyProcessed: false,
        success: true, 
        txHash: signature,
        transaction: {
          signature,
          amount: txResult.amount,
          from: txResult.from,
          to: txResult.to,
        }
      });
    } catch (error) {
      console.error("Find payment by reference error:", error);

      // Handle specific errors
      if (error instanceof Error) {
        if (error.message.includes("not found")) {
          return res.json({ found: false, message: "Payment not found yet. Please complete the transaction." });
        }
      }

      res.status(400).json({ 
        error: error instanceof Error ? error.message : "Failed to find payment" 
      });
    }
  });

  // Cashout routes
  app.post("/api/cashout/request", authenticateToken, requireApproved, async (req: AuthRequest, res) => {
    try {
      const { withdrawalAddress, amountGlory, tokenType } = createCashoutRequestSchema.parse(req.body);
      const userId = req.user!.id;

      // Check user balance
      const user = await storage.getUser(userId);
      if (!user || user.gloryBalance < amountGlory) {
        return res.status(400).json({ error: "Insufficient GLORY balance" });
      }

      // Calculate token amount (for MVP, use 1:1 ratio or configure exchange rate)
      const exchangeRate = 1; // 1 GLORY = 1 USDC (adjust as needed)
      const amountToken = (amountGlory * exchangeRate).toString();

      // Create cashout request
      const request = await storage.createCashoutRequest({
        userId,
        withdrawalAddress,
        amountGlory,
        amountToken,
        tokenType: tokenType || "USDC",
        status: "pending"
      });

      // Create event log
      await storage.createCashoutEvent({
        cashoutRequestId: request.id,
        fromStatus: "created",
        toStatus: "pending",
        actorUserId: userId,
        notes: "Cashout request created"
      });

      res.json({ request });
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Failed to create cashout request" });
    }
  });

  app.get("/api/cashout/requests", authenticateToken, requireApproved, async (req: AuthRequest, res) => {
    try {
      const requests = await storage.getCashoutRequests({ userId: req.user!.id });
      res.json({ requests });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch cashout requests" });
    }
  });

  app.get("/api/cashout/requests/:id", authenticateToken, async (req: AuthRequest, res) => {
    try {
      const request = await storage.getCashoutRequest(req.params.id);
      if (!request) {
        return res.status(404).json({ error: "Cashout request not found" });
      }

      // Check if user owns the request or is admin
      if (request.userId !== req.user!.id && req.user!.role !== "admin") {
        return res.status(403).json({ error: "Unauthorized" });
      }

      const events = await storage.getCashoutEvents(request.id);
      res.json({ request, events });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch cashout request" });
    }
  });

  // Contest routes
  app.get("/api/contests", async (req, res) => {
    try {
      const { status } = req.query;
      const contests = await storage.getContests(status ? { status: status as string } : undefined);

      // Auto-end contests that have passed their endAt time
      const now = new Date();
      const updatedContests = await Promise.all(
        contests.map(async (contest) => {
          if (contest.status === "active" && new Date(contest.endAt) < now) {
            const updated = await storage.updateContest(contest.id, { status: "ended" });
            return updated || contest;
          }
          return contest;
        })
      );

      // Filter out contests that were auto-ended if user requested a specific status
      const filteredContests = status 
        ? updatedContests.filter(contest => contest.status === status)
        : updatedContests;

      // Flatten prizeDistribution from config for frontend
      const contestsWithPrizes = filteredContests.map(contest => ({
        ...contest,
        prizeDistribution: (contest.config as any)?.prizeDistribution || []
      }));

      res.json(contestsWithPrizes);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch contests" });
    }
  });

  app.get("/api/contests/:id", async (req, res) => {
    try {
      let contest = await storage.getContest(req.params.id);
      if (!contest) {
        return res.status(404).json({ error: "Contest not found" });
      }

      // Auto-end contest if it has passed its endAt time
      const now = new Date();
      if (contest.status === "active" && new Date(contest.endAt) < now) {
        const updated = await storage.updateContest(contest.id, { status: "ended" });
        contest = updated || contest;
      }

      // Get top 10 submissions for this contest
      const topSubmissions = await storage.getTopSubmissionsByContest(contest.id, 10);

      res.json({
        ...contest,
        prizeDistribution: (contest.config as any)?.prizeDistribution || [],
        topSubmissions
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch contest" });
    }
  });

  app.post("/api/admin/contests", authenticateToken, requireAdmin, async (req: AuthRequest, res) => {
    try {
      const contestData = insertContestSchema.parse(req.body);
      const contest = await storage.createContest(contestData);

      // Log admin action
      await storage.createAuditLog({
        actorUserId: req.user!.id,
        action: "CREATE_CONTEST",
        meta: { contestId: contest.id, title: contest.title }
      });

      res.status(201).json(contest);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid input" });
    }
  });

  app.patch("/api/admin/contests/:id", authenticateToken, requireAdmin, async (req: AuthRequest, res) => {
    try {
      let updateData = { ...req.body };

      // Convert date strings to Date objects for Drizzle
      if (updateData.startAt) {
        updateData.startAt = new Date(updateData.startAt);
      }
      if (updateData.endAt) {
        updateData.endAt = new Date(updateData.endAt);
      }

      // If no cover image is provided or it's explicitly set to null/empty, use top voted submission
      if (!updateData.coverImageUrl || updateData.coverImageUrl === '') {
        const topSubmissions = await storage.getTopSubmissionsByContest(req.params.id, 1);
        if (topSubmissions.length > 0 && topSubmissions[0].type === 'image') {
          updateData.coverImageUrl = topSubmissions[0].mediaUrl;
        }
      }

      const updatedContest = await storage.updateContest(req.params.id, updateData);
      if (!updatedContest) {
        return res.status(404).json({ error: "Contest not found" });
      }

      // Reschedule automatic end if endAt was updated and contest is active
      if (updateData.endAt && updatedContest.status === "active") {
        contestScheduler.rescheduleContest(updatedContest.id, updatedContest.endAt);
      }

      // Log admin action
      await storage.createAuditLog({
        actorUserId: req.user!.id,
        action: "UPDATE_CONTEST",
        meta: { contestId: updatedContest.id, updates: req.body }
      });

      res.json(updatedContest);
    } catch (error) {
      console.error("Error updating contest:", error);
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid input" });
    }
  });

  app.patch("/api/admin/contests/:id/activate", authenticateToken, requireAdmin, async (req: AuthRequest, res) => {
    try {
      const contest = await storage.getContest(req.params.id);
      if (!contest) {
        return res.status(404).json({ error: "Contest not found" });
      }

      if (contest.status !== "draft") {
        return res.status(400).json({ error: "Only draft contests can be activated" });
      }

      // Update contest status to active
      const updatedContest = await storage.updateContest(contest.id, { status: "active" });

      // Schedule automatic end for this contest
      if (updatedContest && updatedContest.endAt) {
        contestScheduler.scheduleContestEnd(updatedContest.id, updatedContest.endAt);
      }

      // Log admin action
      await storage.createAuditLog({
        actorUserId: req.user!.id,
        action: "ACTIVATE_CONTEST",
        meta: { contestId: contest.id, title: contest.title }
      });

      res.json({ message: "Contest activated successfully", contest: updatedContest });
    } catch (error) {
      console.error("Error activating contest:", error);
      res.status(500).json({ 
        error: "Failed to activate contest",
        details: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  app.post("/api/admin/contests/:id/end", authenticateToken, requireAdmin, async (req: AuthRequest, res) => {
    try {
      const contest = await storage.getContest(req.params.id);
      if (!contest) {
        return res.status(404).json({ error: "Contest not found" });
      }

      if (contest.status !== "active") {
        return res.status(400).json({ error: "Contest is not active" });
      }

      // Cancel any scheduled automatic distribution
      contestScheduler.cancelJob(contest.id);

      // Distribute rewards using transaction-like approach
      await storage.distributeContestRewards(contest.id);

      // Log admin action
      await storage.createAuditLog({
        actorUserId: req.user!.id,
        action: "END_CONTEST",
        meta: { contestId: contest.id, prizePool: contest.prizeGlory }
      });

      res.json({ message: "Contest ended and rewards distributed successfully" });
    } catch (error) {
      console.error("Error ending contest:", error);
      res.status(500).json({ 
        error: "Failed to end contest and distribute rewards",
        details: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  app.delete("/api/admin/contests/:id", authenticateToken, requireAdmin, async (req: AuthRequest, res) => {
    try {
      const contest = await storage.getContest(req.params.id);
      if (!contest) {
        return res.status(404).json({ error: "Contest not found" });
      }

      await storage.deleteContest(req.params.id);

      // Log admin action
      await storage.createAuditLog({
