@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --pg-primary: #7c3cec;
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(240, 10%, 3.9%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(240, 10%, 3.9%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(240, 10%, 3.9%);
  --primary: hsl(270, 80%, 58%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(240, 4.8%, 95.9%);
  --secondary-foreground: hsl(240, 5.9%, 10%);
  --muted: hsl(240, 4.8%, 95.9%);
  --muted-foreground: hsl(240, 3.8%, 46.1%);
  --accent: hsl(270, 80%, 58%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --success: hsl(142, 76%, 36%);
  --success-foreground: hsl(0, 0%, 100%);
  --border: hsl(240, 5.9%, 90%);
  --input: hsl(240, 5.9%, 90%);
  --ring: hsl(270, 80%, 58%);
  --radius: 0.5rem;
  --font-sans: 'Inter', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: 'JetBrains Mono', monospace;
}

.dark {
  --background: 268 43% 10%;
  --foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 4%, 9%);
  --card-foreground: hsl(0, 0%, 98%);
  --popover: hsl(240, 4%, 9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --primary: hsl(270, 80%, 58%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(240, 5%, 26%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 5%, 16%);
  --muted-foreground: hsl(240, 5%, 64%);
  --accent: hsl(270, 80%, 58%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --success: hsl(142, 76%, 36%);
  --success-foreground: hsl(0, 0%, 100%);
  --border: hsl(240, 5%, 26%);
  --input: hsl(240, 5%, 26%);
  --ring: hsl(270, 80%, 58%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply text-foreground antialiased;
    background-color: #171121;
    font-family: var(--font-sans);
  }

  html {
    background-color: #171121;
  }

  #root {
    background-color: #171121;
    min-height: 100vh;
  }
}

@layer utilities {
  /* Background utilities for the new color */
  .bg-app {
    background-color: #171121;
  }

  .bg-app-light {
    background-color: rgba(23, 17, 33, 0.8);
  }

  .bg-app-lighter {
    background-color: rgba(23, 17, 33, 0.5);
  }

  /* Glow Effects */
  .glow {
    box-shadow:
      0 0 6px var(--pg-primary),
      0 0 14px var(--pg-primary),
      0 0 26px var(--pg-primary),
      0 0 46px var(--pg-primary);
  }

  .glow-border {
    box-shadow: 0 0 5px var(--pg-primary),
      0 0 10px var(--pg-primary),
      0 0 15px var(--pg-primary);
  }

  .text-glow {
    text-shadow: 0 0 5px var(--pg-primary),
      0 0 10px var(--pg-primary);
  }

  .hover\:glow:hover {
    box-shadow:
      0 0 10px var(--pg-primary),
      0 0 20px rgba(124, 60, 236, 0.5),
      0 0 30px rgba(124, 60, 236, 0.2);
  }

  /* Glass Effects - Updated for new background */
  .glass {
    background: rgba(23, 17, 33, 0.6);
    -webkit-backdrop-filter: blur(8px);
    backdrop-filter: blur(8px);
    border: 1px solid rgba(124, 60, 236, 0.25);
  }

  .glass-effect {
    background: rgba(23, 17, 33, 0.5);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .glassmorphism {
    background: rgba(23, 17, 33, 0.6);
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.18);
  }

  /* Focus Ring */
  .focus-ring:focus {
    outline: none !important;
    box-shadow:
      0 0 0 2px rgba(255,255,255,0.2),
      0 0 0 6px rgba(124,60,236,0.5);
  }

  /* Animations */
  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes fadeInScale {
    from {
      opacity: 0;
      transform: scale(0.95);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  @keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
  }

  @keyframes boltPulse {
    0%, 100% {
      transform: scale(1);
    }
    50% {
      transform: scale(1.05);
    }
  }

  @keyframes shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }

  @keyframes gradientShift {
    0%, 100% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
  }

  .animate-fade-in-up {
    animation: fadeInUp 0.8s ease-out forwards;
  }

  .animate-fade-in-scale {
    animation: fadeInScale 0.6s ease-out forwards;
  }

  .animate-pulse-slow {
    animation: boltPulse 3s ease-in-out infinite;
  }

  .shimmer-effect {
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.1),
      transparent
    );
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  /* Button Effects */
  .btn-primary {
    position: relative;
    overflow: hidden;
    transform: translateY(0);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .btn-primary:hover {
    transform: translateY(-2px);
    box-shadow:
      0 10px 25px rgba(124, 60, 236, 0.4),
      0 0 20px rgba(124, 60, 236, 0.3);
  }

  .btn-primary::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.2),
      transparent
    );
    transition: left 0.5s;
  }

  .btn-primary:hover::before {
    left: 100%;
  }

  .btn-secondary {
    position: relative;
    overflow: hidden;
    transform: translateY(0);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    border: 1px solid rgba(124, 60, 236, 0.3);
  }

  .btn-secondary:hover {
    transform: translateY(-2px);
    border-color: rgba(124, 60, 236, 0.6);
    box-shadow:
      0 8px 20px rgba(124, 60, 236, 0.2),
      inset 0 0 20px rgba(124, 60, 236, 0.1);
  }

  /* Group Hover Effects */
  .group .group-hover\:scale-110 {
    transform: scale(1);
    transition: transform 0.3s ease;
  }
  .group:hover .group-hover\:scale-110 {
    transform: scale(1.1);
  }

  .group .group-hover\:opacity-100 {
    opacity: 0;
    transition: opacity 0.25s ease;
  }
  .group:hover .group-hover\:opacity-100 {
    opacity: 1;
  }

  .group .group-hover\:opacity-0 {
    opacity: 1;
    transition: opacity 0.25s ease;
  }
  .group:hover .group-hover\:opacity-0 {
    opacity: 0;
  }

  /* Prompt Bar Animation */
  .prompt-bar {
    position: relative;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .prompt-bar:focus-within {
    transform: scale(1.02);
    box-shadow:
      0 0 0 1px rgba(124, 60, 236, 0.5),
      0 10px 30px rgba(124, 60, 236, 0.2);
  }

  .prompt-bar::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, #7c3cec, #00f6ff);
    transition: all 0.3s ease;
    transform: translateX(-50%);
  }

  .prompt-bar:focus-within::after {
    width: 100%;
  }

  /* Text Gradient */
  .gradient-text {
    background: linear-gradient(
      45deg,
      #ffffff,
      #7c3cec,
      #00f6ff,
      #ffffff
    );
    background-size: 300% 300%;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    animation: gradientShift 4s ease-in-out infinite;
  }

  .gradient-glory {
    background: linear-gradient(135deg, hsl(270, 80%, 58%) 0%, hsl(280, 70%, 48%) 100%);
  }

  /* Line Clamp */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
  line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Animation Delays */
  .delay-100 { animation-delay: 0.1s; }
  .delay-200 { animation-delay: 0.2s; }
  .delay-300 { animation-delay: 0.3s; }
  .delay-400 { animation-delay: 0.4s; }
  .delay-500 { animation-delay: 0.5s; }

  /* Podium Styles */
  .podium-1 {
    height: 200px;
    background: linear-gradient(135deg, hsl(45, 100%, 50%) 0%, hsl(45, 100%, 40%) 100%);
  }

  .podium-2 {
    height: 160px;
    background: linear-gradient(135deg, hsl(0, 0%, 75%) 0%, hsl(0, 0%, 65%) 100%);
  }

  .podium-3 {
    height: 120px;
    background: linear-gradient(135deg, hsl(28, 80%, 52%) 0%, hsl(28, 70%, 42%) 100%);
  }

  .vote-pulse {
    animation: pulse 1s ease-in-out;
  }

  /* Pinterest-style Masonry Grid */
  .masonry-grid {
    columns: 1;
    column-gap: 1rem;
    padding: 0.5rem;
  }

  @media (min-width: 640px) {
    .masonry-grid {
      columns: 2;
      column-gap: 1.25rem;
      padding: 0.75rem;
    }
  }

  @media (min-width: 1024px) {
    .masonry-grid {
      columns: 3;
      column-gap: 1.5rem;
      padding: 1rem;
    }
  }

  @media (min-width: 1280px) {
    .masonry-grid {
      columns: 4;
      column-gap: 1.5rem;
    }
  }

  @media (min-width: 1536px) {
    .masonry-grid {
      columns: 5;
      column-gap: 1.75rem;
    }
  }

  .masonry-grid > * {
    break-inside: avoid;
    margin-bottom: 1rem;
    width: 100%;
    display: inline-block;
    transition: transform 0.2s ease, opacity 0.3s ease;
  }

  .masonry-grid > *:hover {
    transform: translateY(-2px);
  }

  /* Pinterest-style loading animation */
  @keyframes pinterest-fade-in {
    from {
      opacity: 0;
      transform: translateY(20px) scale(0.95);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }

  .masonry-grid > * {
    animation: pinterest-fade-in 0.6s ease-out forwards;
  }

  /* Stagger animation for items */
  .masonry-grid > *:nth-child(1) { animation-delay: 0.1s; }
  .masonry-grid > *:nth-child(2) { animation-delay: 0.2s; }
  .masonry-grid > *:nth-child(3) { animation-delay: 0.3s; }
  .masonry-grid > *:nth-child(4) { animation-delay: 0.4s; }
  .masonry-grid > *:nth-child(5) { animation-delay: 0.5s; }
  .masonry-grid > *:nth-child(6) { animation-delay: 0.6s; }
  .masonry-grid > *:nth-child(7) { animation-delay: 0.7s; }
  .masonry-grid > *:nth-child(8) { animation-delay: 0.8s; }
}

