/**
 * Cloudinary URL transformation utilities for responsive images
 */

export interface CloudinaryTransformOptions {
  width?: number;
  height?: number;
  quality?: 'auto' | 'auto:good' | 'auto:best' | 'auto:eco' | 'auto:low' | number;
  format?: 'auto' | 'jpg' | 'png' | 'webp' | 'avif';
  crop?: 'fill' | 'fit' | 'scale' | 'limit';
}

/**
 * Transform a Cloudinary URL with responsive image optimizations
 * Returns original URL if not a Cloudinary URL or already has transformations
 */
export function transformCloudinaryUrl(
  url: string,
  options: CloudinaryTransformOptions = {}
): string {
  // Only transform Cloudinary URLs
  if (!url.includes('cloudinary.com')) {
    return url;
  }

  // Check if URL already has transformations (avoid double transformation)
  // Look for transformation prefixes: w_, h_, c_, q_, f_, t_, e_, g_, ar_, dpr_, fl_, bo_, if_
  // This catches parameter-based and named transforms but allows folders (e.g., /upload/user_assets/)
  const hasTransformations = /\/upload\/[^/]*(?:w_|h_|c_|q_|f_|t_|e_|g_|ar_|dpr_|fl_|bo_|if_)/.test(url);
  if (hasTransformations) {
    return url; // Already transformed, return as-is
  }

  const {
    width,
    height,
    quality = 'auto:good',
    format = 'auto',
    crop = 'fill'
  } = options;

  // Build transformation string
  const transformations: string[] = [];

  if (quality) {
    transformations.push(`q_${quality}`);
  }

  if (format) {
    transformations.push(`f_${format}`);
  }

  if (width || height || crop) {
    const parts: string[] = [];
    if (crop) parts.push(`c_${crop}`);
    if (width) parts.push(`w_${width}`);
    if (height) parts.push(`h_${height}`);
    transformations.push(parts.join(','));
  }

  // Find 'upload/' in URL and insert transformations after it
  const uploadPattern = /\/upload\//;
  if (uploadPattern.test(url)) {
    const transformStr = transformations.join(',');
    return url.replace(/\/upload\//, `/upload/${transformStr}/`);
  }

  return url;
}

/**
 * Preset transformations for common use cases
 */
export const cloudinaryPresets = {
  /** Thumbnail for cards and grids (400px width) */
  thumbnail: (url: string) => transformCloudinaryUrl(url, { 
    width: 400, 
    crop: 'fill',
    quality: 'auto:good',
    format: 'auto'
  }),

  /** Medium size for detail views (800px width) */
  medium: (url: string) => transformCloudinaryUrl(url, { 
    width: 800, 
    crop: 'fit',
    quality: 'auto:good',
    format: 'auto'
  }),

  /** Large for lightbox/fullscreen (1920px width) */
  large: (url: string) => transformCloudinaryUrl(url, { 
    width: 1920, 
    crop: 'fit',
    quality: 'auto:good',
    format: 'auto'
  }),

  /** Extra small for mobile thumbnails (200px width) */
  xsmall: (url: string) => transformCloudinaryUrl(url, { 
    width: 200, 
    crop: 'fill',
    quality: 'auto:eco',
    format: 'auto'
  })
};

client/src/components/PrivateModeGuard.t.content:
import { useAuth } from "@/lib/auth";
import { useLocation } from "wouter";
import { useEffect } from "react";
import { usePrivateMode } from "@/lib/private-mode-context";

interface PrivateModeGuardProps {
  children: React.ReactNode;
}

export function PrivateModeGuard({ children }: PrivateModeGuardProps) {
  const { data: user, isLoading: authLoading } = useAuth();
  const { privateMode, isLoading: privateModeLoading } = usePrivateMode();
  const [, setLocation] = useLocation();

  useEffect(() => {
    // Wait for both queries to load
    if (authLoading || privateModeLoading) return;

    // If private mode is enabled and user is not authenticated, redirect to login
    if (privateMode && !user) {
      setLocation("/login");
    }
  }, [privateMode, user, authLoading, privateModeLoading, setLocation]);

  // Show loading state while checking
  if (authLoading || privateModeLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-muted-foreground">Loading...</div>
      </div>
    );
  }

  // If private mode is enabled and no user, don't render (redirect will happen)
  if (privateMode && !user) {
    return null;
  }

  // Otherwise, render children
  return <>{children}</>;
}

client/src/lib/private-mode-context.tsx.content:
import { createContext, useContext, ReactNode } from "react";
import { useQuery } from "@tanstack/react-query";

interface PrivateModeContextType {
  privateMode: boolean;
  isLoading: boolean;
}

const PrivateModeContext = createContext<PrivateModeContextType | undefined>(undefined);

export function PrivateModeProvider({ children }: { children: ReactNode }) {
  const { data: privateModeStatus, isLoading } = useQuery<{ privateMode: boolean }>({
    queryKey: ["/api/settings/private-mode"],
    queryFn: async () => {
      const response = await fetch("/api/settings/private-mode");
      if (!response.ok) throw new Error("Failed to fetch private mode status");
      return response.json();
    },
  });

  return (
    <PrivateModeContext.Provider value={{ privateMode: privateModeStatus?.privateMode || false, isLoading }}>
      {children}
    </PrivateModeContext.Provider>
  );
}

export function usePrivateMode() {
  const context = useContext(PrivateModeContext);
  if (context === undefined) {
    throw new Error("usePrivateMode must be used within a PrivateModeProvider");
  }
  return context;
}

server/solana.ts.content:
import { Connection, PublicKey, clusterApiUrl } from '@solana/web3.js';

const network = (process.env.SOLANA_NETWORK || 'devnet') as 'devnet' | 'testnet' | 'mainnet-beta';

const endpoint = process.env.SOLANA_RPC_ENDPOINT || clusterApiUrl(network);

export const solanaConnection = new Connection(endpoint, 'confirmed');

export async function verifySolanaSignature(
  publicKey: string,
  signature: string,
  message: string
): Promise<boolean> {
  try {
    const publicKeyObj = new PublicKey(publicKey);
    const messageBytes = new TextEncoder().encode(message);
    const signatureBytes = Buffer.from(signature, 'base64');

    const nacl = await import('tweetnacl');
    return nacl.sign.detached.verify(
      messageBytes,
      signatureBytes,
      publicKeyObj.toBytes()
    );
  } catch (error) {
    console.error('Failed to verify Solana signature:', error);
    return false;
  }
}

export async function getSolanaBalance(walletAddress: string): Promise<number> {
  try {
    const publicKey = new PublicKey(walletAddress);
    const balance = await solanaConnection.getBalance(publicKey);
    return balance / 1e9; // Convert lamports to SOL
  } catch (error) {
    console.error('Failed to get Solana balance:', error);
    return 0;
  }
}

export async function verifyTransaction(signature: string): Promise<{
  confirmed: boolean;
  amount?: number;
  from?: string;
  to?: string;
  accountKeys?: string[];
}> {
  try {
    const tx = await solanaConnection.getTransaction(signature, {
      commitment: 'confirmed',
      maxSupportedTransactionVersion: 0,
    });

    if (!tx || !tx.meta) {
      return { confirmed: false };
    }

    // Get all account keys (static + loaded addresses for versioned transactions)
    const staticKeys = tx.transaction.message.staticAccountKeys.map(key => key.toBase58());
    const loadedWritable = tx.meta.loadedAddresses?.writable?.map(key => key.toBase58()) || [];
    const loadedReadonly = tx.meta.loadedAddresses?.readonly?.map(key => key.toBase58()) || [];

    // Combine all account keys in the correct order
    const accountKeys = [...staticKeys, ...loadedWritable, ...loadedReadonly];

    // Find the largest balance decrease (actual payer)
    let maxDecrease = 0;
    let payerIndex = 0;

    for (let i = 0; i < tx.meta.preBalances.length; i++) {
      const preBalance = tx.meta.preBalances[i] || 0;
      const postBalance = tx.meta.postBalances[i] || 0;
      const decrease = preBalance - postBalance;

      if (decrease > maxDecrease) {
        maxDecrease = decrease;
        payerIndex = i;
      }
    }

    // Get the actual payer address from the computed index
    const payer = accountKeys[payerIndex];

    // Subtract transaction fee to get actual transfer amount
    const fee = tx.meta.fee || 0;
    const transferAmount = Math.max(0, maxDecrease - fee);

    // Find recipient (account with largest balance increase)
    let maxIncrease = 0;
    let recipientIndex = -1;

    for (let i = 0; i < tx.meta.postBalances.length; i++) {
      if (i === payerIndex) continue; // Skip payer

      const preBalance = tx.meta.preBalances[i] || 0;
      const postBalance = tx.meta.postBalances[i] || 0;
      const increase = postBalance - preBalance;

      if (increase > maxIncrease) {
        maxIncrease = increase;
        recipientIndex = i;
      }
    }

    const recipient = recipientIndex >= 0 ? accountKeys[recipientIndex] : undefined;

    return {
      confirmed: true,
      amount: transferAmount / 1e9, // Convert lamports to SOL
      from: payer,
      to: recipient,
      accountKeys,
    };
  } catch (error) {
    console.error('Failed to verify transaction:', error);
    return { confirmed: false };
  }
}

client/src/context/WalletContextProvider.content:
import { FC, ReactNode, useMemo } from 'react';
import {
  ConnectionProvider,
  WalletProvider,
} from '@solana/wallet-adapter-react';
import { WalletModalProvider } from '@solana/wallet-adapter-react-ui';
import { clusterApiUrl } from '@solana/web3.js';

// Import default wallet adapter styles
import '@solana/wallet-adapter-react-ui/styles.css';

interface Props {
  children: ReactNode;
}

export const WalletContextProvider: FC<Props> = ({ children }) => {
  // Use devnet for testing, can be changed to mainnet-beta for production
  const network = import.meta.env.VITE_SOLANA_NETWORK || 'devnet';
  const endpoint = useMemo(() => {
    // Use custom RPC if provided, otherwise use default cluster API
    const customRpc = import.meta.env.VITE_SOLANA_RPC_ENDPOINT;
    if (customRpc) {
      return customRpc;
    }
    return clusterApiUrl(network as 'devnet' | 'testnet' | 'mainnet-beta');
  }, [network]);

  // Empty array - Wallet Standard automatically detects Phantom, Solflare, Backpack, etc.
  const wallets = useMemo(() => [], []);

  return (
    <ConnectionProvider endpoint={endpoint}>
      <WalletProvider wallets={wallets} autoConnect>
        <WalletModalProvider>
          {children}
        </WalletModalProvider>
      </WalletProvider>
    </ConnectionProvider>
  );
};

client/src/components/payment/SolanaPaym.content:
import { useEffect, useState, useCallback, useMemo } from "react";
import { QRCodeSVG } from "qrcode.react";
import { PublicKey, Keypair } from "@solana/web3.js";
import { encodeURL, createQR, TransferRequestURLFields } from "@solana/pay";
import BigNumber from "bignumber.js";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { Copy, Check, Loader2, ExternalLink } from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface SolanaPaymentProps {
  amount: number;
  currency: "SOL" | "USDC" | "CUSTOM";
  recipient: string; // Platform wallet address
  label: string; // Contest name
  message?: string; // Optional message
  customTokenMint?: string; // For custom SPL tokens
  customTokenDecimals?: number; // For custom SPL tokens
  userId: string; // Current user ID
  contestId: string; // Contest ID for verification
  onSuccess: (txHash: string) => void;
  onCancel?: () => void;
}

export function SolanaPayment({
  amount,
  currency,
  recipient,
  label,
  message,
  customTokenMint,
  customTokenDecimals,
  userId,
  contestId,
  onSuccess,
  onCancel,
}: SolanaPaymentProps) {
  const [paymentUrl, setPaymentUrl] = useState<string>("");
  const [copied, setCopied] = useState(false);
  const [isVerifying, setIsVerifying] = useState(false);
  const [pollingInterval, setPollingInterval] = useState<NodeJS.Timeout | null>(null);
  const { toast } = useToast();

  // Generate unique reference for transaction tracking (stable across renders)
  const reference = useMemo(() => Keypair.generate().publicKey, []);

  // Generate Solana Pay URL
  useEffect(() => {
    try {
      const recipientPubkey = new PublicKey(recipient);

      // Determine SPL token mint if applicable
      let splToken: PublicKey | undefined;

      if (currency === "USDC") {
        // USDC devnet mint address
        splToken = new PublicKey("4zMMC9srt5Ri5X14GAgXhaHii3GnPAEERYPJgZJDncDU");
      } else if (currency === "CUSTOM" && customTokenMint) {
        splToken = new PublicKey(customTokenMint);
      }

      // Create transfer request URL
      const url: TransferRequestURLFields = {
        recipient: recipientPubkey,
        amount: new BigNumber(amount),
        reference: [reference],
        ...(splToken && { splToken }),
        label,
        message: message || `Entry fee for ${label}`,
        memo: `contest:${contestId}:user:${userId}`,
      };

      const encodedUrl = encodeURL(url);
      setPaymentUrl(encodedUrl.toString());
    } catch (error) {
      console.error("Error generating payment URL:", error);
      toast({
        title: "Error",
        description: "Failed to generate payment link",
        variant: "destructive",
      });
    }
  }, [amount, currency, recipient, label, message, customTokenMint, contestId, userId, toast]);

  // Copy payment URL to clipboard
  const copyToClipboard = useCallback(() => {
    navigator.clipboard.writeText(paymentUrl);
    setCopied(true);
    toast({
      title: "Copied!",
      description: "Payment link copied to clipboard",
    });
    setTimeout(() => setCopied(false), 2000);
  }, [paymentUrl, toast]);

  // Open payment URL in wallet
  const openInWallet = useCallback(() => {
    window.open(paymentUrl, "_blank");
  }, [paymentUrl]);

  // Poll backend for payment verification using reference
  const startPolling = useCallback(() => {
    if (pollingInterval) return;

    const interval = setInterval(async () => {
      try {
        const res = await apiRequest("POST", "/api/payment/find-by-reference", {
          reference: reference.toBase58(),
          expectedAmount: amount,
          recipientAddress: recipient,
          contestId,
        });

        const data = await res.json();

        if (data.found && data.success && data.txHash) {
          // Payment verified!
          clearInterval(interval);
          setPollingInterval(null);
          onSuccess(data.txHash);
        }
      } catch (error) {
        console.error("Error checking payment status:", error);
      }
    }, 3000); // Poll every 3 seconds

    setPollingInterval(interval);
  }, [reference, amount, recipient, contestId, onSuccess, pollingInterval]);

  // Cleanup polling on unmount
  useEffect(() => {
    return () => {
      if (pollingInterval) {
        clearInterval(pollingInterval);
      }
    };
  }, [pollingInterval]);

  // Manual verification
  const verifyPayment = useCallback(async () => {
    setIsVerifying(true);

    try {
      const res = await apiRequest("POST", "/api/payment/find-by-reference", {
        reference: reference.toBase58(),
        expectedAmount: amount,
        recipientAddress: recipient,
        contestId,
      });

      const data = await res.json();

      if (data.found && data.success && data.txHash) {
        onSuccess(data.txHash);
        toast({
          title: "Payment Verified!",
          description: "Your entry fee payment has been confirmed",
        });
      } else {
        toast({
          title: "Payment Not Found",
          description: data.message || "Please complete the payment and try again",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error("Error verifying payment:", error);
      toast({
        title: "Verification Error",
        description: error instanceof Error ? error.message : "Failed to verify payment. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsVerifying(false);
    }
  }, [reference, amount, recipient, contestId, onSuccess, toast]);

  const displayAmount = currency === "CUSTOM" && customTokenDecimals
    ? `${amount} (Custom Token)`
    : `${amount} ${currency}`;

  return (
    <Card className="w-full max-w-md mx-auto" data-testid="card-solana-payment">
      <CardHeader>
        <CardTitle>Pay Entry Fee</CardTitle>
        <CardDescription>
          Scan QR code or use the payment link to pay {displayAmount}
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* QR Code */}
        {paymentUrl && (
          <div className="flex justify-center p-4 bg-white rounded-lg" data-testid="container-qr-code">
            <QRCodeSVG value={paymentUrl} size={200} level="H" />
          </div>
        )}

        {/* Payment URL */}
        <div className="space-y-2">
          <p className="text-sm text-muted-foreground">Payment Link:</p>
          <div className="flex gap-2">
            <input
              type="text"
              value={paymentUrl}
              readOnly
              className="flex-1 px-3 py-2 text-sm border rounded-md bg-muted"
              data-testid="input-payment-url"
            />
            <Button
              variant="outline"
              size="icon"
              onClick={copyToClipboard}
              data-testid="button-copy-link"
            >
              {copied ? <Check className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
            </Button>
          </div>
        </div>

        {/* Instructions */}
        <div className="p-3 bg-muted rounded-lg text-sm space-y-1">
          <p className="font-medium">How to pay:</p>
          <ol className="list-decimal list-inside space-y-1 text-muted-foreground">
            <li>Scan QR code with mobile wallet (Phantom, Solflare)</li>
            <li>Or copy link and open in wallet app</li>
            <li>Confirm transaction in your wallet</li>
            <li>Wait for verification (automatic)</li>
          </ol>
        </div>

        {/* Actions */}
        <div className="flex gap-2">
          <Button
            variant="outline"
            className="flex-1"
            onClick={openInWallet}
            data-testid="button-open-wallet"
          >
            <ExternalLink className="h-4 w-4 mr-2" />
            Open in Wallet
          </Button>

          <Button
            variant="default"
            className="flex-1"
            onClick={verifyPayment}
            disabled={isVerifying}
            data-testid="button-verify-payment"
          >
            {isVerifying ? (
              <>
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                Verifying...
              </>
            ) : (
              "Verify Payment"
            )}
          </Button>
        </div>

        {/* Auto-polling notice */}
        <p className="text-xs text-center text-muted-foreground">
          Payment verification happens automatically. You can also verify manually.
        </p>

        {/* Cancel */}
        {onCancel && (
          <Button
            variant="ghost"
            className="w-full"
            onClick={onCancel}
            data-testid="button-cancel-payment"
          >
            Cancel
          </Button>
        )}
      </CardContent>
    </Card>
  );
}
