              onClick={() => {
                setBulkDeleteContestsDialogOpen(false);
                setDeleteContestsConfirmText("");
              }}
              data-testid="cancel-bulk-delete-contests"
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
