import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

export function formatPrizeAmount(amount: string | number | null | undefined): string {
  if (amount === null || amount === undefined) return '0';

  const num = typeof amount === 'string' ? parseFloat(amount) : amount;

  if (isNaN(num)) return '0';

  // Convert to string and remove trailing zeros after decimal point
  const str = num.toString();

  // If it's a whole number (no decimal point or ends with .0, .00, etc)
  if (str.includes('.')) {
    return parseFloat(str).toString(); // This automatically removes trailing zeros
  }

  return str;
}

