  getUsersWithFilters(filters: { status?: string; role?: string }): Promise<UserWithStats[]>;
  getUsersByIds(ids: string[]): Promise<User[]>;
  bulkDeleteUsers(ids: string[]): Promise<number>;

  // Contests
