    supportsStyleReferenceImages: false,
    supportsPromptUpsampling: true,
    supportsSafetyTolerance: true,
    supportsCfg: false,
    supportsPromptStrength: false,
    supportsLeonardoStyle: false,
    supportsContrast: false,
    supportsGenerationMode: false,
