    supportsNumImages: false,
  },

  "sd-3.5-large": {
    id: "sd-3.5-large",
    name: "Stable Diffusion 3.5 Large",
    description: "Latest SD with improved quality and detail",
    replicateModel: "stability-ai/stable-diffusion-3.5-large",
    costPerImage: 0.055,

    supportsAspectRatio: true,
