export interface GenerateImageOptions {
  prompt: string;
  model?: string;
  seed?: number;
  userId?: string; // Required for Supabase uploads of large images

  // Dimension options
  aspectRatio?: string;
  width?: number;
  height?: number;
  resolution?: string;

  // Output options
  outputFormat?: string;
  outputQuality?: number;

  // Prompt modifiers
  negativePrompt?: string;
  promptUpsampling?: boolean;
  promptEnhance?: boolean;
  magicPromptOption?: string;

  // Image input
  imageInput?: string | string[];
  mask?: string;

  // Style options (Ideogram)
  styleType?: string;
  stylePreset?: string;
  styleReferenceImages?: string[];

  // Leonardo options
  leonardoStyle?: string;
  contrast?: string;
  generationMode?: string;
  numImages?: number;

  // Flux options
  safetyTolerance?: number;

  // Stable Diffusion options
  cfg?: number;
  promptStrength?: number;
}

export interface GeneratedImage {
  url: string;
  cloudinaryUrl?: string;
  cloudinaryPublicId?: string;
  parameters: Record<string, any>;
}

export async function generateImage(options: GenerateImageOptions): Promise<GeneratedImage[]> {
  const {
    prompt,
    model = "flux-1.1-pro",
    seed,
    userId,
    aspectRatio = "1:1",
    width,
    height,
    resolution,
    outputFormat = "webp",
    outputQuality = 80,
    negativePrompt,
    promptUpsampling = false,
    promptEnhance = false,
    magicPromptOption = "Auto",
    imageInput,
    mask,
    styleType,
    stylePreset,
    styleReferenceImages,
    leonardoStyle,
    contrast = "medium",
    generationMode = "standard",
    numImages = 1,
    safetyTolerance = 2,
    cfg = 5,
    promptStrength = 0.85,
  } = options;

  // Get model configuration
  const modelConfig = AI_MODELS[model] || AI_MODELS["flux-1.1-pro"];

  console.log(`Generating AI image with ${modelConfig.name}...`, { prompt, model });

  try {
    // Helper function to build input parameters for a single generation
    const buildInputParams = () => {
      const input: any = {
        prompt,
      };

      // Add seed if provided
      if (seed !== undefined) {
        input.seed = seed;
      }

      // Model-specific parameter handling
      switch (model) {
        case "ideogram-v3":
          if (resolution && resolution !== "None") {
            input.resolution = resolution;
          } else if (aspectRatio) {
            input.aspect_ratio = aspectRatio;
          }
          if (magicPromptOption) input.magic_prompt_option = magicPromptOption;
          if (styleType && styleType !== "None") input.style_type = styleType;
          if (stylePreset && stylePreset !== "None") input.style_preset = stylePreset;
          if (imageInput) input.image = imageInput;
          if (mask) input.mask = mask;
          if (styleReferenceImages && styleReferenceImages.length > 0) {
            input.style_reference_images = styleReferenceImages;
          }
          break;

        case "nano-banana":
          if (aspectRatio) input.aspect_ratio = aspectRatio;
          if (outputFormat) {
            // Nano Banana only supports jpg/png
            input.output_format = outputFormat === "webp" ? "jpg" : outputFormat;
          }
          if (imageInput) {
            // Handle single image or array
            input.image_input = Array.isArray(imageInput) ? imageInput : [imageInput];
          }
          break;

        case "flux-1.1-pro":
          if (aspectRatio === "custom" && width && height) {
            input.aspect_ratio = "custom";
            input.width = width;
            input.height = height;
          } else if (aspectRatio) {
            input.aspect_ratio = aspectRatio;
          }
          if (outputFormat) input.output_format = outputFormat;
          if (outputQuality) input.output_quality = outputQuality;
          if (promptUpsampling) input.prompt_upsampling = true;
          if (safetyTolerance) input.safety_tolerance = safetyTolerance;
          if (imageInput) input.image_prompt = imageInput;
          break;

        case "sd-3.5-large":
          if (aspectRatio) input.aspect_ratio = aspectRatio;
          if (outputFormat) input.output_format = outputFormat;
          if (negativePrompt) input.negative_prompt = negativePrompt;
          if (cfg) input.cfg = cfg;
          if (imageInput) {
            input.image = imageInput;
            input.prompt_strength = promptStrength;
          }
          break;

        case "leonardo-lucid":
          if (aspectRatio) input.aspect_ratio = aspectRatio;
          if (leonardoStyle && leonardoStyle !== "none") input.style = leonardoStyle;
          if (contrast) input.contrast = contrast;
          if (generationMode) input.generation_mode = generationMode;
          if (promptEnhance !== undefined) input.prompt_enhance = promptEnhance;
          if (numImages) input.num_images = numImages;
          break;
      }

      return input;
    };

    // Leonardo supports native multi-image generation
    const leonardoSupportsMultiImage = model === "leonardo-lucid";

    // Collect all Replicate outputs
    let allOutputs: any[] = [];

    if (leonardoSupportsMultiImage) {
      // Leonardo: Single API call with num_images parameter
      const input = buildInputParams();
      console.log("Replicate input parameters:", input);

      const output = await replicate.run(
        modelConfig.replicateModel as `${string}/${string}`,
        { input }
      ) as any;

      allOutputs = Array.isArray(output) ? output : [output];
    } else {
      // Other models: Call API multiple times for each image
      console.log(`Generating ${numImages} image(s) with sequential API calls...`);

      for (let i = 0; i < numImages; i++) {
        const input = buildInputParams();
        console.log(`Replicate input parameters (image ${i + 1}/${numImages}):`, input);

        const output = await replicate.run(
          modelConfig.replicateModel as `${string}/${string}`,
          { input }
        ) as any;

        // Each call returns a single image (or array with one image)
        if (Array.isArray(output)) {
          allOutputs.push(...output);
        } else {
          allOutputs.push(output);
        }
      }
    }

    if (!allOutputs || allOutputs.length === 0) {
      throw new Error("No image generated");
    }

    // Extract URLs from Replicate response (always an array now)
    let imageUrls: string[] = [];

    // Process all images in the array
    for (const item of allOutputs) {
      let url: string;

      if (typeof item === 'object' && item !== null) {
        if (typeof item.url === 'function') {
          const urlResult = await item.url();
          url = typeof urlResult === 'object' && urlResult.href ? urlResult.href : String(urlResult);
        } else if (typeof item.url === 'string') {
          url = item.url;
        } else {
          throw new Error("FileOutput object missing url property");
        }
      } else if (typeof item === 'string') {
        url = item;
      } else {
        throw new Error("Invalid output format from Replicate");
      }

      imageUrls.push(url);
    }

    console.log(`AI image(s) generated successfully: ${imageUrls.length} image(s)`);

    // Upload all images to Cloudinary
    const results: GeneratedImage[] = [];

    for (let i = 0; i < imageUrls.length; i++) {
      const imageUrl = imageUrls[i];
      let uploadedUrl: string | undefined;
      let cloudinaryPublicId: string | undefined;

      try {
        const uploadResult = await downloadAndUploadToCloudinary(imageUrl, false, userId);
        uploadedUrl = uploadResult.url;
        cloudinaryPublicId = uploadResult.publicId;

        if (uploadResult.isSupabase) {
          console.log(`Image ${i + 1}/${imageUrls.length} uploaded to Supabase:`, uploadedUrl);
        } else {
          console.log(`Image ${i + 1}/${imageUrls.length} uploaded to Cloudinary:`, uploadedUrl);
        }
      } catch (uploadError) {
        console.error(`Upload failed for image ${i + 1}, using Replicate URL:`, uploadError);
      }

      results.push({
        url: uploadedUrl || imageUrl,
        cloudinaryUrl: uploadedUrl, // Can be either Cloudinary or Supabase URL
        cloudinaryPublicId,
        parameters: {
          model: modelConfig.id,
          ...options, // Include all original parameters
        },
      });
    }

    return results;
  } catch (error) {
    console.error("Replicate API error:", error);
    throw new Error(
      `Failed to generate image: ${error instanceof Error ? error.message : "Unknown error"}`
    );
  }
}

async function downloadAndUploadToCloudinary(imageUrl: string, isUpscaled: boolean = false, userId?: string): Promise<{
  url: string;
  publicId: string | null;
  isSupabase: boolean;
}> {
  const SIZE_LIMIT = 10 * 1024 * 1024; // 10MB in bytes

  try {
    // Download image
    const response = await fetch(imageUrl);
    if (!response.ok) {
      throw new Error(`Failed to download image: ${response.statusText}`);
    }

    const arrayBuffer = await response.arrayBuffer();
    const buffer = Buffer.from(arrayBuffer);
    const fileSizeBytes = buffer.byteLength;

    console.log(`Image size: ${(fileSizeBytes / 1024 / 1024).toFixed(2)}MB`);

    // If image is >= 10MB, upload to Supabase temporary bucket
    if (fileSizeBytes >= SIZE_LIMIT) {
      console.log(`Image is >= 10MB, uploading to Supabase temporary storage`);

      if (!userId) {
        throw new Error('userId required for Supabase upload');
      }

      const { uploadImageToSupabase } = await import('./supabase');
      const timestamp = Date.now();
      const generationId = `gen_${timestamp}`;

      // Upload to Supabase temporary bucket
      const { url: supabaseUrl } = await uploadImageToSupabase(
        imageUrl,
        userId,
        generationId,
        `v${timestamp}`
      );

      return {
        url: supabaseUrl,
        publicId: null, // No Cloudinary public ID for Supabase images
        isSupabase: true
      };
    }

    // If image is < 10MB, upload to Cloudinary
    console.log(`Image is < 10MB, uploading to Cloudinary`);

    const tempDir = path.join(process.cwd(), "temp");
    if (!fs.existsSync(tempDir)) {
      fs.mkdirSync(tempDir, { recursive: true });
    }

    const tempFilePath = path.join(tempDir, `ai-${Date.now()}.png`);
    await writeFileAsync(tempFilePath, buffer);

    const uploadOptions: any = {
      resource_type: "image",
      folder: "5best-ai-generated",
      quality: isUpscaled ? "auto:eco" : "auto:good",
      fetch_format: "auto",
    };

    // For upscaled images, use upload_stream with proper stream handling
    let result: any;
    try {
      if (isUpscaled) {
        uploadOptions.transformation = [
          { quality: 80, fetch_format: "jpg" } // Light compression to balance quality and size
        ];
        console.log("Using upload_stream for upscaled image");

        // Create read stream and upload using stream API for proper synchronization
        result = await new Promise((resolve, reject) => {
          const readStream = fs.createReadStream(tempFilePath);
          const uploadStream = cloudinary.uploader.upload_stream(
            uploadOptions,
            (error: any, result: any) => {
              if (error) {
                reject(error);
              } else {
                // Wait for read stream to fully close before resolving
                readStream.on('close', () => {
                  console.log("Upload stream complete and read stream closed");
                  resolve(result);
                });
                // If stream already closed, resolve immediately
                if (readStream.closed) {
                  console.log("Upload complete, stream already closed");
                  resolve(result);
                }
              }
            }
          );

          readStream.on('error', reject);
          uploadStream.on('error', reject);
          readStream.pipe(uploadStream);
        });
      } else {
        result = await cloudinary.uploader.upload(tempFilePath, uploadOptions);
      }

      return {
        url: result.secure_url,
        publicId: result.public_id,
        isSupabase: false
      };
    } finally {
      // Always clean up temp file, whether upload succeeded or failed
      if (fs.existsSync(tempFilePath)) {
        await unlinkAsync(tempFilePath);
      }
    }
  } catch (error) {
    throw error;
  }
}

export async function upscaleImage(
  imageUrl: string,
  options?: { scale?: number; faceEnhance?: boolean; userId?: string }
): Promise<{
  url: string;
  cloudinaryUrl?: string;
  cloudinaryPublicId?: string;
}> {
  try {
    console.log("Starting image upscaling with Real-ESRGAN:", imageUrl);

    const input: any = {
      image: imageUrl,
      scale: options?.scale || 4,
    };

    if (options?.faceEnhance !== undefined) {
      input.face_enhance = options.faceEnhance;
    }

    const output = await replicate.run(
      "nightmareai/real-esrgan:c15c48c0e85a93f3d4e283ac6ca684ce180d94d1975783663c747e7bfa6f5e5c",
      { input }
    );

    console.log("Real-ESRGAN raw output:", output);
    console.log("Real-ESRGAN output type:", typeof output);

    let upscaledUrl: string;

    // Replicate can return different types depending on the model
    if (typeof output === "string") {
      upscaledUrl = output;
    } else if (Array.isArray(output) && output.length > 0) {
      upscaledUrl = output[0];
    } else if (output && typeof output === "object") {
      // Try to extract URL from FileOutput or other object types
      const outputObj = output as any;
      console.log("Processing object output. Has .url?", !!outputObj.url, "Type of .url:", typeof outputObj.url);
      console.log("Has .output?", !!outputObj.output, "Type of .output:", typeof outputObj.output);

      if (outputObj.url && typeof outputObj.url === "function") {
        console.log("Calling .url() method...");
        const urlResult = await outputObj.url();
        console.log("Result from .url():", urlResult, "Type:", typeof urlResult);
        // .url() can return a URL object, extract the href string
        if (typeof urlResult === "string") {
          upscaledUrl = urlResult;
        } else if (urlResult && urlResult.href) {
          upscaledUrl = urlResult.href;
        } else if (urlResult && typeof urlResult.toString === "function") {
          upscaledUrl = urlResult.toString();
        } else {
          throw new Error(`Invalid URL result from .url() method: ${typeof urlResult}`);
        }
      } else if (outputObj.url && typeof outputObj.url === "string") {
        console.log("Using .url string property");
        upscaledUrl = outputObj.url;
      } else if (outputObj.output) {
        console.log("Using .output property");
        upscaledUrl = outputObj.output;
      } else {
        throw new Error(`Unsupported Real-ESRGAN output format: ${typeof output}`);
      }
    } else {
      throw new Error(`Real-ESRGAN did not return a valid image URL. Got: ${typeof output}`);
    }

    if (!upscaledUrl || typeof upscaledUrl !== "string") {
      throw new Error(`Failed to extract valid URL from Real-ESRGAN output: ${upscaledUrl}`);
    }

    console.log("Real-ESRGAN upscaling completed:", upscaledUrl);

    // Upload upscaled image to Cloudinary with quality optimization
    // Use quality:85 to balance file size (<10MB for free tier) and quality
    let uploadedUrl: string | undefined;
    let cloudinaryPublicId: string | undefined;

    try {
      const uploadResult = await downloadAndUploadToCloudinary(upscaledUrl, true, options?.userId);
      uploadedUrl = uploadResult.url;
      cloudinaryPublicId = uploadResult.publicId;

      if (uploadResult.isSupabase) {
        console.log("Upscaled image uploaded to Supabase:", uploadedUrl);
      } else {
        console.log("Upscaled image uploaded to Cloudinary:", uploadedUrl);
      }
    } catch (uploadError) {
      console.error("Upload failed for upscaled image, using Replicate URL:", uploadError);
    }

    return {
      url: uploadedUrl || upscaledUrl,
      cloudinaryUrl: uploadedUrl, // Can be either Cloudinary or Supabase URL
      cloudinaryPublicId,
    };
  } catch (error) {
    console.error("Real-ESRGAN upscaling error:", error);
    throw new Error(
      `Failed to upscale image: ${error instanceof Error ? error.message : "Unknown error"}`
    );
  }
}

