                    <td className="p-4 font-medium">AI Upscaling</td>
                    {sortedTiers.map((tier) => (
                      <td key={tier.id} className="p-4 text-center" data-testid={`td-upscale-${tier.slug}`}>
                        {tier.canUpscale ? (
